import React, { useState } from 'react';
import './Experience.css';

const experienceData = [
  {
    title: 'Software Developer at Company X',
    details: [
      'Developed and maintained web applications using React and Node.js.',
      'Collaborated with cross-functional teams to define and achieve project goals.',
      'Implemented responsive design principles to ensure compatibility across various devices.'
    ],
  },
  // ... add more experiences here
];

const Experience = () => {
  const [selectedExperience, setSelectedExperience] = useState(experienceData[0]);
  const [selectedIndex, setSelectedIndex] = useState(0);

  const handleExperienceClick = (experience, index) => {
    setSelectedExperience(experience);
    setSelectedIndex(index);
  };

  return (
    <div className="experience-container">
      <div className="experience-list">
        <div className="experience-names">
          <h1><u>Experience</u></h1>
          <ul>
            {experienceData.map((experience, index) => (
              <li 
                key={index} 
                onClick={() => handleExperienceClick(experience, index)}
                className={selectedIndex === index ? 'selected' : ''}
              >
                {experience.title}
              </li>
            ))}
          </ul>
        </div>
        <div className="experience-details">
          <ul>
            {selectedExperience.details.map((detail, index) => (
              <li key={index}>{detail}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Experience;
