import React, { useState } from 'react';
import './Experience.css';

const experienceData = [
  {
    title: 'General IT intern | First Solar',
    details: [
        'Engage in a year-long IT Business Intelligence internship at First Solar, handling data troubleshooting, syncing, and report migration from Tableau to PowerBI.',
        'Work with the IT data services team on diverse tasks, including enhancing existing solutions and crafting new PowerBI dashboards and reports, ensuring effective time management and clear communication.',
        'Complete the internship with a presentation of accomplishments and project summaries to the leadership team, leveraging skills in accountability, communication, and collaboration, and demonstrating a willingness to learn and adapt within different IT roles'
        ],
  },
  {
    title: 'IT Technician at University of Toledo',
    details: [
        'Successfully resolved over 100 software and IT-related challenges with an average solution time of less than 24 hours, addressing issues like devices not powering on, water damage, hardware replacement, screen replacement, network connectivity problems, and virus infections',
        'Diagnose and debug staff and lab desktops of the University, employing techniques such as PXE boot and archive directory management to address issues like slowdowns, re-imaging, networking, and related issues'
    ],
  }
];

const Experience = () => {
  const [selectedExperience, setSelectedExperience] = useState(experienceData[0]);
  const [selectedIndex, setSelectedIndex] = useState(0);

  const handleExperienceClick = (experience, index) => {
    setSelectedExperience(experience);
    setSelectedIndex(index);
  };

  return (
    <div className="experience-container">
      <div className="experience-list">
        <div className="experience-names">
          <h1><u>Experience</u></h1>
          <ul>
            {experienceData.map((experience, index) => (
              <li 
                key={index} 
                onClick={() => handleExperienceClick(experience, index)}
                className={selectedIndex === index ? 'selected' : ''}
              >
                {experience.title}
              </li>
            ))}
          </ul>
        </div>
        <div className="experience-details">
          <ul>
            {selectedExperience.details.map((detail, index) => (
              <li key={index}>{detail}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Experience;
