import React, { useState } from 'react';
import './Projects.css';
import Certificate8 from '../../constants/Certificates/Certificate8.jpg';
import Certificate9 from '../../constants/Certificates/Certificate9.jpg';
import Certificate10 from '../../constants/Certificates/Certificate10.jpg';

const Projects = () => {
    const [currentImage, setCurrentImage] = useState(0);
    const images = [Certificate8, Certificate9, Certificate10];
    const technologies = ["React", "Node.js", "MongoDB"]; // Example technologies

    return (
        <div className="project-container">
            <div className="project-description">
                <h2>U-Eats</h2>
                <p>Deployed an online-ordering restaurant website on AWS amplify in collaboration with my roommate Dec
                    2023. The website is expected to increase monthly sales by 5%. Integrated React.js and Node.js technologies to enhance the website's performance, as evidenced by
                    improved loading times and interactivity. Implemented Stripe payment for secure and efficient online transactions.
                </p>
                <div className="project-technologies">
                    {technologies.map((tech, index) => (
                        <span key={index} className="tech-item">{tech}</span>
                    ))}
                </div>
            </div>
            <div className="project-images">
                <img src={images[currentImage]} alt="Project" />
                <div className="image-navigation">
                    {images.map((_, index) => (
                        <span
                            key={index}
                            className={`nav-dot ${index === currentImage ? 'active' : ''}`}
                            onClick={() => setCurrentImage(index)}
                        ></span>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Projects;
