import React, { useState } from 'react';
import './ContactMe.css'; // Ensure the CSS file is correctly imported

const ContactMe = () => {
  const [showForm, setShowForm] = useState(false);
  const [email, setEmail] = useState('');
  const [subject, setSubject] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Logic for form submission goes here
    console.log({ email, subject, message });
    setEmail('');
    setSubject('');
    setMessage('');
  };

  return (
    <section className="contact-container">
      <div className="top-section" id="contact-me">
        <h1>Get in Touch</h1>
        <p>I would be delighted to hear from you if you're interested in discussing new opportunities.</p>
        <button className="get-in-touch-button" onClick={() => setShowForm(!showForm)}>Say Hello</button>
      </div>

      {showForm && (
        <form className="email-form" onSubmit={handleSubmit}>
          <h2>Send Me An Email</h2>
          <div className="form-group">
            <label htmlFor="email">Your Email:</label>
            <input 
              type="email" 
              id="email" 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
              required 
            />
          </div>
          <div className="form-group">
            <label htmlFor="subject">Subject:</label>
            <input 
              type="text" 
              id="subject" 
              value={subject} 
              onChange={(e) => setSubject(e.target.value)} 
              required 
            />
          </div>
          <div className="form-group">
            <label htmlFor="message">Message:</label>
            <textarea 
              id="message" 
              value={message} 
              onChange={(e) => setMessage(e.target.value)} 
              required 
            ></textarea>
          </div>
          <button type="submit">Send Email</button>
        </form>
      )}

      <div className="links">
        <a href="https://www.linkedin.com/in/your-linkedin-username" target="_blank" rel="noopener noreferrer">LinkedIn</a>
        <a href="https://github.com/your-github-username" target="_blank" rel="noopener noreferrer">GitHub</a>
        {/* Add more links as needed */}
      </div>


    </section>
  );
};

export default ContactMe;
