import './ContactMe.css'; // Make sure to import the CSS file

const ContactMe = () => {
  const [showForm, setShowForm] = useState(false);
  const [email, setEmail] = useState('');
  const [subject, setSubject] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Logic to handle form submission goes here
    console.log({ email, subject, message });
    setEmail('');
    setSubject('');
    setMessage('');
  };

  return (
    <section className="contact-container">
      <div className="top-section">
        <button className="get-in-touch-button" onClick={() => setShowForm(!showForm)}>Get in Touch</button>
        <div className="links">
          <a href="https://www.linkedin.com/in/your-linkedin-username" target="_blank" rel="noopener noreferrer">LinkedIn</a>
          <a href="https://github.com/your-github-username" target="_blank" rel="noopener noreferrer">GitHub</a>
          {/* Add more links as needed */}
        </div>
      </div>

      {showForm && (
        <form className="email-form" onSubmit={handleSubmit}>
          {/* Your form fields here */}
        </form>
      )}
    </section>
  );
};

export default ContactMe;
