import React, { useState } from 'react';
import './ContactMe.css'; // Ensure this is imported

const ContactMe = () => {
  const [email, setEmail] = useState('');
  const [subject, setSubject] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you'll need to send this data to your server
    // to process and send an email
    console.log({ email, subject, message });
    // Clear the form fields
    setEmail('');
    setSubject('');
    setMessage('');
  };

  return (

    <section className="contact-container">
    <div className="links-container">
      <h2>Contact Links</h2>
      <ul>
        <li>
          <a href="https://www.linkedin.com/in/your-linkedin-username" target="_blank" rel="noopener noreferrer">LinkedIn</a>
        </li>
        <li>
          <a href="https://github.com/your-github-username" target="_blank" rel="noopener noreferrer">GitHub</a>
        </li>
        {/* Add more links as needed */}
      </ul>
    </div>

    <form className="email-form" onSubmit={handleSubmit}>
      <h2>Send Me An Email</h2>
      <div className="form-group">
        <label htmlFor="email">Your Email:</label>
        <input 
          type="email" 
          id="email" 
          value={email} 
          onChange={(e) => setEmail(e.target.value)} 
          required 
        />
      </div>
      <div className="form-group">
        <label htmlFor="subject">Subject:</label>
        <input 
          type="text" 
          id="subject" 
          value={subject} 
          onChange={(e) => setSubject(e.target.value)} 
          required 
        />
      </div>
      <div className="form-group">
        <label htmlFor="message">Message:</label>
        <textarea 
          id="message" 
          value={message} 
          onChange={(e) => setMessage(e.target.value)} 
          required 
        ></textarea>
      </div>
      <button type="submit">Send Email</button>
    </form>
    </section>
  );
};

export default ContactMe;
